import React from 'react';
import { Provider } from 'react-redux';
import {
  BrowserRouter as Router,
  useLocation,
  Route,
  Switch,
} from 'react-router-dom';
import { setStore } from '../../store';
// import { routes } from '../../routes';
// import Bugsnag from '@bugsnag/js';
// import BugsnagPluginReact from '@bugsnag/plugin-react';
// import { renderRoutes } from 'react-router-config';
import { useTransition, animated } from 'react-spring';
import { Navbar } from '../Navbar';
import { AppContainer } from './styles';
import { Home } from '../Home';
import { Browse } from '../Browse';
import { ProductPage } from '../ProductPage';

export const CuziRoute: FC<any> = ({
  component: Page,
  title,
  path,
  publicRoute = false,
}) => {
  const history = useHistory();
  const location = useLocation();
  // TODO: Fix type
  const currentUser = getCurrentUser(location.state as any);

  return (
    <Route path={path}>
      {!!publicRoute || !!(currentUser?.roleTypes ?? []).includes('ADMIN') ? (
        <Layout>
          <Header style={{ background: '#e7e7e7', minHeight: '70px' }}>
            <Navbar currentUser={currentUser} />
          </Header>
          <Layout>
            <Sider style={{ height: 'calc(100vh - 140px)' }}>
              <Sidebar />
            </Sider>
            <Content>
              <Page history={history} currentUser={currentUser} title={title} />
            </Content>
          </Layout>
          <Footer style={{ background: '#e7e7e7' }}>Footer</Footer>
        </Layout>
      ) : (
        <Redirect to="/login" /> // Redirect to home if not authenticated
      )}
    </Route>
  );
};

export const App = (props: any) => {
  const location = useLocation();
  console.log('location:', location);

  const transitions = useTransition(location, (location) => location.pathname, {
    from: { opacity: 0, transform: 'translate(100%, 0)' },
    enter: { opacity: 1, transform: 'translate(0%, 0)' },
    leave: { opacity: 0, transform: 'translate(-50%, 0)' },
  });

  return (
    <Provider store={setStore()}>
      <Router>
        <AppContainer>
          <Navbar />
          {transitions.map(({ item: location, props, key }) => (
            <animated.div key={location.key} style={props}>
              {/* {renderRoutes(routes, props, { location: location })} */}
            </animated.div>
          ))}
        </AppContainer>
      </Router>
    </Provider>
  );
};
